import React from 'react';
import ReactDOM from 'react-dom';

function BookList(props) {
  const books = props.books;
  const listItems = books.map((book) => <li>{book}</li>);

  return(
    <ul>{listItems}</ul>
  );
}

const login = (
  <form method="POST" action="auth.php">
    <input type="text" name="usr" placeholder="Username" autocomplete="off"></input>
    <input type="password" name="pwd" placeholder="Password" autocomplete="off"></input>
    <button type="submit">Login</button>  
    <button type="submit" formaction="register.php">Register</button>
  </form>
);

ReactDOM.render(login, document.getElementById('login'));

const books = ['The Republic', 'Candide', 'Beyond Good & Evil', 'Crime & Punishment', 'Gulag Archipelago']
ReactDOM.render(<BookList books={books}></BookList>, document.getElementById('booklist'))
