import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

const books = ['The Republic', 'Candide', 'Beyond Good & Evil', 'Crime & Punishment', 'Gulag Archipelago'];

function Recommender() {
  const [recommendations, setRecommendations] = useState(books);

  const onLogin = setRecommendations;

  return(
    <div>
      <hr/>
      <Credentials onLogin={onLogin}/>
      <hr/>
      <BookList books={recommendations} />
    </div>
  );
}

function Credentials(props) {
  const [username, setUsername] = useState(null);
  const [isLogged, setIsLogged] = useState(false);

  const handleLogin = async(event) => {
    event.preventDefault();
    let usr = event.target.parentElement.usr.value;
    let pwd = event.target.parentElement.pwd.value;

    const post = {'usr': {usr}, 'pwd': {pwd}};
    const reply = await axios.post('login.php', post);
    console.log(reply.data);
  }

  const handleRegister = event => {
    event.preventDefault();
    console.log(event.target.parentElement.usr.value);

  }

  if(!isLogged) {
    return(
      <form>
        <input type="text" name="usr" placeholder="Username" autocomplete="off"></input>
        <input type="password" name="pwd" placeholder="Password" autocomplete="off"></input>
        <button type="submit" onClick={handleLogin}>Login</button>  
        <button type="submit" onClick={handleRegister}>Register</button>
      </form>
    );
  } else {
    return(
      <div>Logged In! {props.data}</div>
    );
  }
}

function BookList(props) {
  const books = props.books;
  const listItems = books.map((book) => <li>{book}</li>);

  return(
    <ul>{listItems}</ul>
  );
}

ReactDOM.render(<Recommender/>, document.getElementById('root'));
